    console.warn('This name has been deprecated. Please use PLUGIN_GITHUB_APP_ID instead.\n');
    nenv.PLUGIN_GITHUB_APP_ID = env.GITHUB_APP_ID;
  }
  if (env.GITHUB_SECRET) {
