exports.removeUser = function (req, res) {
  User.findOne({email: req.body.email}, function (err, user) {
    if (err || !user) {
      req.flash('admin', 'Failed to find user');
      return res.redirect('/admin/users');
    }
    Project.collection.remove({
      creator: user._id
