 */
function requireBody(key, req, res) {
  var val = req.body[key];

  if (val === undefined) {
    return res.status(400).json({
      status: 'error',
      errors: [`you must supply parameter ${key}`]
    });
  }

  return val;
}

module.exports = requireBody;

